const commands = $input.all().map((item) => item.json);
const sourceAndDest = commands.map((command) => {
  const [source, destination] = command.rawText.split(" ").slice(1);
  return { source, destination };
});

return [
  {
    json: {
      label: "source",
      source: sourceAndDest[0]['source'],
    },
  },
  {
    json: {
      label: "destination",
      destination: sourceAndDest[0]['destination']

    },
  }
];

return sourceAndDest;

const rawText = $json.Body?.trim();
if (!rawText) {
  return {
    json: { error: "‚ùå No message content received." }
  };
}

const cleanText = rawText.replace(/\s+/g, " "); // collapse multiple spaces
const [command, ...args] = cleanText.split(" ");
const path = args.join(" ").trim(); // combine everything after the command

return {
  json: {
    command: command.toUpperCase(),
    args: [path],
    rawText: rawText
  }
};

